import { ADD_SUBSCRIBER, REMOVE_SUBSCRIBER } from "./type";

const initialState = {
  count: 370,
};

const subscriberReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_SUBSCRIBER:
      return {
        ...state,
        count: state.count + 1,
      };
    case REMOVE_SUBSCRIBER:
      return {
        ...state,
        count: state.count - 1,
      };
  }
};
export default subscriberReducer;
